package jp.afw.core.log;import jp.afw.persistence.context.Context;/** * このクラスは、標準出力ロガーのインスタンス生成を行うファクトリクラスです。 *  * @since 1.0.0 * @version 1.0.0 2013/06/28 * @author Kawakicchi */public final class StdoutLoggerFactory extends LoggerFactory {	/**	 * コンストラクタ	 */	public StdoutLoggerFactory() {	}	@Override	protected void doLoad(String aName, Context aContext) {	}	@Override	protected Logger doCreate() {		return new StdoutLogger(LoggerFactory.class);	}	@Override	protected Logger doCreate(final String aName) {		return new StdoutLogger(aName);	}	@Override	protected Logger doCreate(final Class<?> aClass) {		return new StdoutLogger(aClass);	}}